{
    int i, j, *p, *q;
    p = malloc(10 * sizeof(int));
    if (p == NULL)
    {
        return 1;
    }
    for (i = 0; i < 10; ++i) 
    {
        p[i] = i;
    }    
    q = p;
    my_free(p); // p is freed
    j = 0;

    // checks if q is illegal before running the for loop
    if (q == 0 || q == NULL)
    {
        exit(1);
    }
    
    for (i = 0; i < 10; ++i)
    {
        // Use after free here (note the statement q = p), read of q[i] is illegal
        j += q[i]; 
    }
        
    return j;
}

