{
    int tmp_inited;
    unsigned long irq_flags;
    spin_lock_irqsave( &p_sbuf_mgr->sbuf_lock, irq_flags );
    tmp_inited = p_sbuf_mgr->sbuf_inited;
    spin_unlock_irqrestore( &p_sbuf_mgr->sbuf_lock, irq_flags );
    return tmp_inited;
}

