int func(int x, int y)
{
  int ret_var;
  ret_var = 1;
  bool bool_var;

  if ( ( ret_var == 1 ) || ( bool_var = true ) )
  {
    // non-compliant
  }

  if ( ( ret_var = test() ) > 1 )
  {
    // non-compliant
  }

  while (0) {
    // ...
